import styled from "@emotion/styled";

const travelList = [
  {
    country: "오사카",
    desc: "일본에서 세 번째로 큰 도시로 맛있는 음식과 활기찬 분위기가 특징이에요.",
  },
  {
    country: "도쿄",
    desc: "도쿄는 일본의 수도이자 독특한 매력으로 전 세계에서 사랑받는 대도시입니다",
  },
  {
    country: "방콕",
    desc: "화려한 사원과 쇼핑몰이 유명하고, 물가도 저렴한 편이에요.",
  },
];

function App() {
  return (
    <Layout>
      <Container>
        <Header>
          <Logo src="img/logo.svg" alt="로고" />
          <UtilsBox>
            <IconWithMargin src="/img/alarm.svg" alt="알람" />
            <Icon src="/img/mypage.svg" alt="알람" />
          </UtilsBox>
        </Header>
        <Body>
          <Wrapper1>
            <Title>지금 가기 좋아요</Title>
            <ImageBox>
              {travelList.map((item, index) => (
                <Box key={index}>
                  <ImageWrapper>
                    <Image />
                    <CountryName>{item.country}</CountryName>
                  </ImageWrapper>
                  <Desc>{item.desc}</Desc>
                </Box>
              ))}
            </ImageBox>
          </Wrapper1>
        </Body>
      </Container>
    </Layout>
  );
}

export default App;

const Layout = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;

const Container = styled.div`
  width: 380px;
  height: 100vh;
  padding: 20px;
  background-color: aliceblue;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
`;

const Logo = styled.img``;

const UtilsBox = styled.div`
  display: flex;
`;

const Icon = styled.img``;

const IconWithMargin = styled(Icon)`
  margin-right: 15px;
`;

const Body = styled.div``;

const Wrapper1 = styled.div``;

const Title = styled.div`
  margin-bottom: 16px;
  font-size: 20px;
  font-weight: 800;
`;

const ImageBox = styled.div`
  display: flex;
  overflow-x: auto;

  /* 스크롤바 숨기기 (선택사항) */
  &::-webkit-scrollbar {
    display: none;
  }
`;

const Box = styled.div`
  margin-right: 10px;
`;

const ImageWrapper = styled.div`
  position: relative;
`;

const Image = styled.img`
  width: 200px;
  height: 120px;
  margin-bottom: 8px;
  background-color: gray;
  border-radius: 16px;
`;

const CountryName = styled.div`
  position: absolute;
  bottom: 20px;
  left: 12px;
  font-size: 20px;
  font-weight: 700;
  color: white;
`;

const Desc = styled.div`
  font-size: 12px;
`;
