import React from "react";
import Slider from "react-slick";
import styled from "@emotion/styled";

const travelList = [
  { image: "/img/oaska.svg", title: "오사카" },
  { image: "/img/tokyo.svg", title: "도쿄" },
  { image: "/img/bangkok.svg", title: "방콕" },
  { image: "/img/singapore.svg", title: "싱가포르" },
  { image: "/img/london.svg", title: "런던" },
  { image: "/img/newyork.svg", title: "뉴욕" },
  { image: "/img/paris.svg", title: "파리" },
  { image: "/img/berlin.svg", title: "베를린" },
];

function Carousel() {
  const settings = {
    infinite: true,
    speed: 500,
    rows: 2,
    slidesPerRow: 2,
    dots: true,
  };

  return (
    <CarouselWrapper>
      <StyledSlider {...settings}>
        {travelList.map((item, index) => (
          <CardWrapper key={index}>
            <Card>
              <Image src={item.image} alt={item.title} />
              <Label>{item.title}</Label>
            </Card>
          </CardWrapper>
        ))}
      </StyledSlider>
    </CarouselWrapper>
  );
}

export default Carousel;

const CarouselWrapper = styled.div`
  width: 100%;
  max-width: 380px;
  margin: 0 auto;
`;

const StyledSlider = styled(Slider)`
  .slick-slide > div {
    padding: 8px;
  }
`;

const CardWrapper = styled.div`
  display: flex;
  justify-content: center;
`;

const Card = styled.div`
  width: 150px;
  height: 100px;
  border-radius: 16px;
  background-color: #f5f0e6;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;

const Image = styled.img`
  width: 40px;
  height: 40px;
  object-fit: cover;
  margin-bottom: 8px;
`;

const Label = styled.div`
  font-size: 14px;
  font-weight: 600;
`;
