import React from "react";
import Slider from "react-slick";
import styled from "@emotion/styled";

const travelList = [
  { image: "/img/oaska.svg", title: "오사카" },
  { image: "/img/tokyo.svg", title: "도쿄" },
  { image: "/img/bangkok.svg", title: "방콕" },
  { image: "/img/seoul.svg", title: "서울" },
  { image: "/img/sydney.svg", title: "시드니" },
  { image: "/img/newyork.svg", title: "뉴욕" },
];

function Carousel() {
  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    arrows: false,
    dots: true,
  };

  return (
    <SliderWrapper>
      <StyledSlider {...settings}>
        {travelList.map((item, index) => (
          <Card key={index}>
            <Image src={item.image} />
            <Label>{item.title}</Label>
          </Card>
        ))}
      </StyledSlider>
    </SliderWrapper>
  );
}

export default Carousel;

const SliderWrapper = styled.div`
  width: 100%;
  margin: 0 auto;
`;

const StyledSlider = styled(Slider)`
  .slick-slide {
    display: flex;
    justify-content: center;
  }
`;

const Card = styled.div`
  width: 100px;
  height: 120px;
  background-color: #f5f0e6;
  border-radius: 16px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
`;

const Image = styled.img`
  width: 40px;
  height: 40px;
  object-fit: cover;
  margin-bottom: 8px;
`;

const Label = styled.div`
  font-size: 14px;
  font-weight: 600;
`;
