import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styled from "@emotion/styled";

const travelList = [
  {
    image: "/img/singapore.jpg",
    title: "싱가폴",
    desc: "초고층 빌딩과 푸른 열대정원이 어우러져 미래와 전통이 교차하는 곳.",
  },
  {
    image: "/img/berlin.jpg",
    title: "베를린",
    desc: "역사와 예술이 살아 숨 쉬는 독일의 수도.",
  },
  // ... 추가
];

export default function Carousel() {
  const settings = {
    centerMode: true,
    centerPadding: "40px",
    slidesToShow: 3,
    speed: 500,
    infinite: true,
  };

  return (
    <CarouselWrapper>
      <StyledSlider {...settings}>
        {travelList.map((item, index) => (
          <Slide key={index}>
            <Card>
              <Image src={item.image} />
              <Overlay>
                <Title>{item.title}</Title>
                <Desc>{item.desc}</Desc>
              </Overlay>
            </Card>
          </Slide>
        ))}
      </StyledSlider>
    </CarouselWrapper>
  );
}

const CarouselWrapper = styled.div`
  width: 100%;
  margin: 0 auto;
  max-width: 380px;
`;

const StyledSlider = styled(Slider)`
  .slick-slide > div {
    display: flex;
    justify-content: center;
  }

  .slick-slide {
    transform: scale(0.9);
    transition: transform 0.3s ease;
    opacity: 0.5;
  }

  .slick-center {
    transform: scale(1);
    opacity: 1;
  }
`;

const Slide = styled.div`
  width: 100%;
`;

const Card = styled.div`
  position: relative;
  width: 240px;
  height: 340px;
  border-radius: 24px;
  overflow: hidden;
  background-color: aquamarine;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const Overlay = styled.div`
  position: absolute;
  bottom: 16px;
  left: 16px;
  color: white;
`;

const Title = styled.div`
  font-size: 20px;
  font-weight: 800;
`;

const Desc = styled.div`
  font-size: 12px;
  margin-top: 4px;
`;
