export default function Carousel() {
  return (
    <ScrollContainer>
      {travelList.map((item, index) => (
        <CardWrapper key={index}>
          <Card>
            <Image src={item.image} />
            <Overlay>
              <CountryName>{item.country}</CountryName>
              <Desc>{item.desc}</Desc>
            </Overlay>
          </Card>
        </CardWrapper>
      ))}
    </ScrollContainer>
  );
}

const Container = styled.div`
  display: flex;
  overflow-x: auto;
  white-space: nowrap;

  /* 스크롤바 숨기기 (선택사항) */
  &::-webkit-scrollbar {
    display: none;
  }
`;

const Card = styled.div`
  position: relative;
  width: ${props => (props.isCenter ? "200px" : "75px")};
  height: ${props => (props.isCenter ? "221px" : "221px")};
  margin-right: 16px;
  border-radius: 16px;
  overflow: hidden;
  transition: all 0.3s ease;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const Overlay = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  padding: 12px;
  width: 100%;
  background: linear-gradient(
    180deg,
    rgba(0, 0, 0, 0) 30%,
    rgba(0, 0, 0, 0.6) 100%
  );
  color: white;
`;

const CountryName = styled.div`
  font-size: 16px;
  font-weight: bold;
  margin-bottom: 4px;
`;

const Desc = styled.div`
  font-size: 12px;
  line-height: 1.4;
`;

